/*
Verifica que las cantidades de encabezado e Item
*/
SELECT
    ET.TranNum,
    ET.Transferir,
    COUNT(IT.Linea) AS CantidadRegistros
FROM
    RV_TBL_SIP_ENCABEZADO_TRANSFERENCIA_P ET
JOIN
    RV_TBL_SIP_ITEM_TP IT ON ET.TranNum = IT.TranNum
GROUP BY
    ET.TranNum, ET.Transferir
HAVING
    ET.Transferir <> COUNT(IT.Linea);

